import { Component, Inject } from '@angular/core';
import {MAT_DIALOG_DATA} from "@angular/material/dialog";

@Component({
  selector: 'app-error-dialog',
  templateUrl: './error-dialog.component.html',
  styleUrls: ['./error-dialog.component.scss']
})
export class ErrorDialogComponent {

  constructor(@Inject(MAT_DIALOG_DATA) public data: HTMLImageElement) {
  }

}
<h1 mat-dialog-title style="color: red;">Erro ao carregar os cursos!!!</h1>
<div mat-dialog-content>
    <img class="error-image" mat-card-image src="./assets/img/eto..bleh.jpg" alt="eto...bleh"/>
</div>
<div mat-dialog-actions align="center">
  <button mat-stroked-button mat-dialog-close>Fechar</button>
</div>
