import { Component } from '@angular/core';
import {Curso} from "../model/curso";
import {CursosService} from "../services/cursos.service";
import {catchError, Observable, of} from "rxjs";
import {MatDialog} from "@angular/material/dialog";
import {ErrorDialogComponent} from "../../shared/components/error-dialog/error-dialog.component";

@Component({
  selector: 'app-cursos',
  templateUrl: './cursos.component.html',
  styleUrls: ['./cursos.component.scss']
})
export class CursosComponent {

  listaCursos$: Observable<Curso[]>;
  loading$: Observable<boolean>;
  displayedColumns = ['name', 'tag'];

  //cursosService: CursosService;

  constructor(private cursosService: CursosService, public dialog: MatDialog) {

    //this.cursosService = new CursosService(HttpClient);
    this.listaCursos$ = this.cursosService.list().pipe(
      catchError(error => {
        console.log(error);
        return of([])
      })
    );
    this.loading$ = this.cursosService.loadingSubject;
  }

  onError(errorMsg: string) {
    this.dialog.open(ErrorDialogComponent)
  }

}
<mat-card>

  <mat-toolbar color = "primary">Lista de Cursos</mat-toolbar>



<table mat-table [dataSource]="listaCursos$" class="mat-elevation-z8">

  <ng-container matColumnDef="name">
    <th mat-header-cell *matHeaderCellDef> Nome </th>
    <td mat-cell *matCellDef="let element"> {{element.name}}</td>
  </ng-container>

  <ng-container matColumnDef="tag">
    <th mat-header-cell *matHeaderCellDef> Tag </th>
    <td mat-cell *matCellDef="let element"> {{element.tag}}</td>
  </ng-container>

  <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
  <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

</table>

  <div class="loading-spinner" *ngIf="loading$ | async">
    <mat-spinner></mat-spinner>
  </div>

</mat-card>
